USE agri_db;

-- 第一步：创建表结构（如果表不存在）
CREATE TABLE IF NOT EXISTS `pests_diseases` (
                                                `id` INT AUTO_INCREMENT PRIMARY KEY,
                                                `crop_id` INT NOT NULL COMMENT '1:水稻, 2:小麦, 3:玉米, 4:棉花, 5:其他',
                                                `name` VARCHAR(100) NOT NULL,
                                                `symptoms` TEXT,
                                                `prevention` TEXT,
                                                `image_url` VARCHAR(255) DEFAULT 'https://images.unsplash.com/photo-1536655639669-25f00e396677?w=600'
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;

