INSERT INTO pests_diseases (crop_id, name, symptoms, prevention) VALUES
                                                                     (1, '稻瘟病', '叶部产生菱形病斑，中心灰白边缘褐色；穗颈受害变褐腐烂，造成白穗。', '选用三环唑、稻瘟灵或春雷霉素喷雾；消灭越冬菌源。'),
                                                                     (1, '水稻纹枯病', '叶鞘出现云纹状病斑，边缘暗褐中间灰白；严重时植株倒伏，叶片枯死。', '合理密植，控制氮肥；喷施井冈霉素、噻呋酰胺。'),
                                                                     (1, '二化螟', '幼虫蛀食茎秆，分蘖期造成枯鞘、枯心苗，孕穗期造成白穗。', '安装杀虫灯；幼虫孵化高峰期喷施阿维菌素、氯虫苯甲酰胺。'),
                                                                     (1, '稻飞虱', '成虫、若虫群集稻丛基部刺吸汁液，导致植株变黄、枯死，呈“冒穿”状。', '选用吡虫啉、烯啶虫胺、吡蚜酮；保护蜘蛛等天敌。'),
                                                                     (1, '水稻恶苗病', '病株细高，叶片淡黄，节间拉长，根系发育不良。', '严格进行种子消毒（咪鲜胺浸种）；拔除病株。'),
                                                                     (2, '小麦赤霉病', '穗部颖壳出现水浸状腐烂，产生粉红色霉层；病粒干瘪。', '抽穗扬花期喷施多菌灵、氰烯菌酯或戊唑醇；选用抗病品种。'),
                                                                     (2, '小麦锈病', '叶片上产生鲜黄色条状粉堆（夏孢子堆），后期产生黑色疱斑。', '选用三唑酮、戊唑醇、丙环唑喷雾；种植抗病品种。'),
                                                                     (2, '小麦蚜虫', '群集于叶片、茎秆和穗部吸食汁液，分泌蜜露；传播黄矮病。', '吡虫啉、啶虫脒、抗蚜威喷雾；保护瓢虫等天敌。'),
                                                                     (2, '小麦全蚀病', '根部变黑腐烂，地上部出现“黑脚”和白穗，易拔起。', '硅噻菌胺拌种；轮作倒茬；土壤消毒。'),
                                                                     (3, '玉米大斑病', '叶片出现长梭形青灰色或褐色大斑，严重时叶片枯死。', '喷施百菌清、多菌灵或代森锰锌；种植抗病品种。'),
                                                                     (3, '玉米螟', '幼虫钻蛀茎秆和果穗，造成折茎、雄穗折断和虫穗。', '心叶末期撒施辛硫磷颗粒剂；释放赤眼蜂；Bt制剂防治。'),
                                                                     (3, '玉米丝黑穗病', '果穗变成黑粉包，内部混有丝状物；雄穗也可能受害。', '戊唑醇、烯唑醇拌种；适当迟播；拔除病株。'),
                                                                     (3, '草地贪夜蛾', '幼虫暴食叶片，钻蛀心叶和果穗，头部有倒Y字形纹。', '甲氨基阿维菌素苯甲酸盐、氯虫苯甲酰胺喷雾；性诱剂诱杀。'),
                                                                     (4, '棉铃虫', '幼虫蛀食蕾、花、铃，造成落蕾、落铃和烂铃。', '种植Bt抗虫棉；喷施高效氯氟氰菊酯、甲维盐；杨树枝把诱蛾。'),
                                                                     (4, '棉花枯萎病', '维管束变褐，叶片网状褪绿或变黄，植株萎蔫枯死。', '选用抗病品种；轮作倒茬；恶霉灵灌根。'),
                                                                     (4, '棉红蜘蛛', '叶片出现黄白斑点，后变红褐色，严重时落叶光杆。', '阿维菌素、哒螨灵、炔螨特喷雾。'),
                                                                     (5, '大豆食心虫', '幼虫蛀入豆荚咬食豆粒，造成虫食粒，严重影响品质。', '敌敌畏熏蒸；菊酯类农药喷雾。'),
                                                                     (5, '大豆胞囊线虫病', '植株矮黄，根系产生大量白色或黄色小颗粒（胞囊），称“火龙秧”。', '轮作（与禾本科）；种衣剂包衣；抗病品种。'),
                                                                     (5, '高粱丝黑穗病', '全穗变成黑粉包，内部有黑色丝状物（维管束残余）。', '戊唑醇拌种；轮作；拔除病株深埋。'),
                                                                     (5, '花生叶斑病', '叶片出现褐色或黑色斑点，严重时叶片脱落。', '百菌清、多菌灵、代森锰锌喷雾。');